dbToJson.BLOB = function dbToJsonBLOB(dbValue) {
    /* in  buffer                           */
    /* out "iVBORg=="                       */
    /*--->'Edm.Binary'                      */
    if ( dbValue === null) {
        return null;
    }
    return dbValue.toString('base64');
};

